        // }).then(response=>response.json()).then(response=>{
        //     console.log("New seed added!");
        //     console.log(response);
        // }).catch(err=>{
        //     console.log("Error adding new seed!");
        //     console.log(err);
        // })

    };


    return (
        <div>
            <form>
                <input placeholder='Title' onChange={(e)=>{setTitle(e.target.value)}}  type="text"/>
                <input placeholder='Description' onChange={(e)=>{setDescription(e.target.value)}}  type="text"/>
                <textarea placeholder='Body' onChange={(e)=>{setBody(e.target.value)}} name="body" id="body"></textarea>
                <input onChange={(e)=>{setImageURL(e.target.files[0])}} id="imageURL" name="imageURL" type="file" accept="image/*" />
