import React ,{useState,useEffect} from 'react';
import '../../App.css';
import Cards from '../Cards';
import HeroSection from '../HeroSection';
import Footer from '../Footer';
import CheckUserTokenValidity from "../shared/LoginChecker";
function Home() {

    const TOKEN = localStorage.getItem('TOKEN');
    const [isLoggedIn,setIsLoggedIn]  = useState(false);
    const [userDetails,setUserDetails] = useState({});
    const [userDetailsLoaded,setUserDetailsLoaded] = useState(false);
    function setIsUserLoggedIn(bool,userDetails){
        setUserDetails(userDetails);
        setIsLoggedIn(bool);
    }

    useEffect(()=>{
        CheckUserTokenValidity(TOKEN).then(response=>response.data).then(response=>{
            setIsLoggedIn(response.success);
            setUserDetails(response.userData);
            setUserDetailsLoaded(true);
            console.log(isLoggedIn);
            console.log(response.userData);

        }).catch(err=>{
                console.log(err);
            }


        );
    },[])

  return (
    <>
        {userDetailsLoaded ?
            (<><HeroSection userDetails={userDetails} isLoggedIn={isLoggedIn} loggedInCallback={setIsUserLoggedIn}/>
            <Cards userDetails={userDetails} isLoggedIn={isLoggedIn} loggedInCallback={setIsUserLoggedIn}/>
            <Footer />
            </>)
            :null}
    </>
  );
}

export default Home;
