import React from 'react';
import axios from 'axios';
import {BACKEND_URL} from "../../constants";


export default async function CheckUserTokenValidity(TOKEN){
    await axios.get(BACKEND_URL+'user-routes/check-user-validity',{
        headers: {
            'Authorization' : `Bearer ${TOKEN}`
        }
    }).then(response=>{
        return response;
    }).catch(err=>{
        return err
    })
}
