import axios from "axios";
import styled from "styled-components";
import Cookies from "js-cookie";

const Login = () => {
  const [username,setUsername] = useState('aryamannsingh9@gmail.com');
  const [password,setPassword] = useState('15052002')
  const [err, setErr] = useState("");

  const changeHandler = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log(user);
  };

  const submitHandler = async (e) => {
    //   We don't want the page to reload.
    e.preventDefault();
    axios
      .post("http://localhost:8010/user-routes/sign-in", user)
      .then((data) => {
        console.log(data);
        console.log(user);
      })
      .catch((event) => console.log(event.message));
  };

  console.log(err);
  return (
    <div>
      <form onSubmit={submitHandler}>
        <div>
          <Label>Email: </Label>
          <Input
            name="email"
            type="email"
            placeholder="Enter your Email Address"
          />
        </div>
        <div>
          <Label>Password: </Label>
          <Input
            name="password"
            type="password"
            placeholder="Enter a password"
          />
        </div>
        <Input type="submit" value="Login" />
        {/* <Button onClick={protect}>Get Crawling!</Button> */}
      </form>
    </div>
  );
};

const Label = styled.label`
  font-size: 2rem;
  padding: 2rem;
  border-radius: 20%;
`;

const Input = styled.input`
